.navigation[data-v-15fcebda] {
  position: absolute;
  top: 50px;
  right: 0;
  left: -120%;
  bottom: 0;
  height: auto;
  width: 33em;
  height: 200px;
  width: 14em;
  list-style-type: none;
   -webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.19), 0 2px 6px rgba(0, 0, 0, 0.23);
           box-shadow: 0 5px 10px rgba(0, 0, 0, 0.19), 0 2px 6px rgba(0, 0, 0, 0.23);
  background: #FFF;
  z-index: 999;
}
.navigation a[data-v-15fcebda] {
  color: #5A5651;
}
.navigation a[data-v-15fcebda]:hover {
  background-color: #F1EDE9;
}
.navigation .nuxt-link-active[data-v-15fcebda],
.navigation .nuxt-link-exact-active[data-v-15fcebda] {
  color: #FFF;
  background-color: #7d8066;
  font-weight: 700;
}
.navigation .nuxt-link-active[data-v-15fcebda]:hover {
  background: #7d8066;
}
.navigation li[data-v-15fcebda] {
  height: 50px;
}
.navigation li a[data-v-15fcebda] {
  height: 100%;
  line-height: 50px;
  display: block;
  padding-left: 1.5em;
}
.navTrigger[data-v-15fcebda] {
  position: absolute;
  clip: rect(0, 0, 0, 0);
}
#menuButton[data-v-15fcebda] {
}

/* -- Menyknapp ( Hamburgermenu )--*/
#menuButton[data-v-15fcebda] {
  color: #5A5651;
  width: 55px;
  height: 50px;
  line-height: 50px;
  position: absolute;
  display: block;
  float: left;
  padding-left:5px;
  text-align: center;
  text-transform: uppercase;
  font-size: 2.1em;
  cursor: pointer;
  /* Strechar ut ikonen lite  - Progressive Enhancement -   https://stackoverflow.com/a/15262227/4178864 */
  -webkit-transform: scale(1.4, 1.0);
  -ms-transform: scale(1.4, 1.0);
  transform: scale(1.4, 1.0);
  -webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.19), 0 2px 6px rgba(0, 0, 0, 0.23);
          box-shadow: 0 5px 10px rgba(0, 0, 0, 0.19), 0 2px 6px rgba(0, 0, 0, 0.23);
  background: #FFF;
  z-index: 999;
}
.navTrigger+label[data-v-15fcebda],
.navigation[data-v-15fcebda],
.navBackground[data-v-15fcebda] {
  -webkit-transition: left 0.2s;
  -o-transition: left 0.2s;
  transition: left 0.2s;
}
.navTrigger:checked~.navigation[data-v-15fcebda] {
  display: block;
  left: 0;
}
@media(min-width:55em) {
#menuButton[data-v-15fcebda],
  .navTrigger[data-v-15fcebda],
  .navBackground[data-v-15fcebda] {
    display: none;
}
.navigation[data-v-15fcebda] {
    top:0;
