    height: 50px;
}
.nav li a[data-v-15fcebda] {
    padding: 0;
}

  /* defining a width for the menu items */
.nav .home[data-v-15fcebda] {
    width: 5em;
}
.nav .teaHouses[data-v-15fcebda],
  .teaVarieties[data-v-15fcebda] {
    width: 9em;
}
.nav .openingHours[data-v-15fcebda] {
    width: 10em;
}
.nav ul[data-v-15fcebda] {
    text-align: center;
}
.nav ul li[data-v-15fcebda] {
    margin: 0;
    float: left;
}
}
