.headerImageWrapper {
    position:relative;
    z-index:1;
    width:100%;
    height:auto;
    max-height:35em;
    overflow: hidden;
    -webkit-transition:all 0.3s cubic-bezier(.25,.8,.25,1);
    -o-transition:all 0.3s cubic-bezier(.25,.8,.25,1);
    transition: all 0.3s cubic-bezier(.25,.8,.25,1);
}
.headerImage img {
  width:110%;
  float:right;
  clear:both;
}
.box { /* Tip taken from a tutorial/article http://www.mademyday.de/css-height-equals-width-with-pure-css.html */ 
    position: relative;
    background-size: cover;
    background-repeat: no-repeat;
    height:100%;
}
.box:before {
    content:'';
    display:block;
    padding-top:50%;
}
.content{   
    position:  absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    text-align: center;
    overflow: hidden
}
.headlineText {
    position:absolute;
    top:0;
    left:50%;
    right:0;
    bottom:0;
    margin-left:-24em;
    z-index:5;
    padding-top: 100px;
    font-weight: 400;
    font-size: .3em;
    line-height: 1.4;
    color: #656263;
}
.headlineText h3 {
    font-size:4em;
}
.headlineText, .headlineText p {
    text-align: left;
}
.headlineText p {
    font-size:3em;
    width:15em; 
    margin:0 auto;
}
@media(min-width:18.6em) {
.headlineText {
    font-size: .4em;
    text-align:center;
}
.headlineText p {
    font-size:2.5em;
    text-align:justify;
    width:auto;
}
}
@media(min-width:40em) {
.headlineText {
    width:48em;
    margin-left:-24em;
    font-size: .5em;
    line-height: 1.2;
    padding-top: 110px;
}
}
@media(min-width:55em) {
.headlineText {
    font-size: .8em;
    line-height: 1.4;
    padding-top: 150px;
}
.headlineText p {
    font-size:2em;
    margin-top:10px;
}
}
@media(min-width:62em) {
}
@media(min-width:80em) {
}

